mapper.select:

	select * from (
	select rownum rnum, board,num,title,content,writer,regidate,updatedate,viewcnt,filepath,adminselect from (
	select 'event/eventRead' board,num,title,content,writer,regidate,updatedate,viewcnt,filepath,adminselect from boardEV 
	union
	select 'list/boardread' board,num,title,content,writer,regidate,updatedate,viewcnt,filepath,adminselect from boardFR )
	) where rnum between #{startNum} and #{endNum}

	
	select * from (
	select rownum
	rnum,num,gdnum,writer,content,regidate,updatedate,recommend,assistyes,assistno,title
	from
	(select
	gamereview.num,gamereview.gdnum,gamereview.writer,gamereview.content,gamereview.regidate,gamereview.updatedate,gamereview.recommend,gamereview.assistyes,gamereview.assistno,gamedetail.title
	from (
    select * from gamereview 
    ) gamereview,gamedetail where gamereview.gdnum=gamedetail.num order by gamereview.num desc)
	) where rnum between #{startNum} and #{endNum}
