mapper.select:
select count(*) from (
	select floor(months_between(sysdate, userinfo.birth) /12) age from buylist,userinfo where buylist.id=userinfo.id and buylist.gdnum=#{num}
) where age  <  20

select count(*) from (
	select floor(months_between(sysdate, userinfo.birth) /12) age from buylist,userinfo where buylist.id=userinfo.id and buylist.gdnum=#{num}
) where age between 20 and 29

select count(*) from (
	select floor(months_between(sysdate, userinfo.birth) /12) age from buylist,userinfo where buylist.id=userinfo.id and buylist.gdnum=#{num}
) where age between 30 and 39

select count(*) from (
	select floor(months_between(sysdate, userinfo.birth) /12) age from buylist,userinfo where buylist.id=userinfo.id and buylist.gdnum=#{num}
) where age  >=  40
